import React, { Component } from "react";
import "../style/Posters.css";
export default class PosterEachMovie extends Component {
  constructor(props) {
    super();
  }
  render() {
    let moviePic =
      "http://www.omdbapi.com/?i=" + this.props.imdb_id + "&apikey=e2b14d16";
    // let alternativePic =
    //   "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.shutterstock.com%2Fsearch%2Fbook%2Bmissing%2Bpage&psig=AOvVaw0969xLMyEgibK2yAbpHt6b&ust=1601950291307000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCNDAhYWwnOwCFQAAAAAdAAAAABAD";
    console.log(moviePic);
    return (
      <div>
        <img
          src={moviePic.Poster}
          className="moviePicture"
          alt="oops! Can not find the source"
        />
        <div className="movieTitle">{this.props.title}</div>
        <div className="movieReleaseYear">
          Published at {this.props.release_year}
        </div>
      </div>
    );
  }
}

