import React, { Component } from "react";
import "../style/Posters.css";
export default class PosterEachMovie extends Component {
  constructor(props) {
    super();
  }
  render() {
    let moviePic =
      "http://img.omdbapi.com/?i=" + this.props.imdb_id + "&apikey=e2b14d16";
    return (
      <div>
        <img src={moviePic} className="" alt="moviePicture" />
        <h2>{this.props.title}</h2>
      </div>
    );
  }
}
