import React, { Component } from 'react'
import PageNavbar from "./PageNavbar";
import {MapContainer, GeoJSON, Popup, Marker} from 'react-leaflet'
import GeojsonData from './../countries.json'
export default class COVID19_world_map extends Component {

    constructor(){
        this.state = {

        }
    }
    onEachCountry = (country, layer) => {
        const countryName = country.properties.ADMIN
        // console.log(countryName)
        layer.bindPopup(countryName)
        layer.on({
            click:(event)=> {
                console.log('clicked')
            }
        })
    }
    countryStyle = {
        fillColor : 'blue',
        fillOpacity: 0.8,
        color : 'black'
    }

    render() {
        return ( 
            <div>             
                <MapContainer style={{height:"80vh"}} zoom={10} center={[20, 100]} scrollWheelZoom={false}>
                    <GeoJSON 
                    style = {this.countryStyle}
                    data={GeojsonData.features}
                    />
                    <Marker position={[20, 100]}>
                        <Popup>
                            {}
                        </Popup>
                    </Marker>
                </MapContainer>
            </div>
        )
    }
}
