import React, { Component } from 'react'
import PageNavbar from "./PageNavbar";
import {MapContainer, GeoJSON, Popup, Marker, TileLayer} from 'react-leaflet'
import GeojsonData from './../countries.json'
import 'leaflet/dist/leaflet.css'
export default class COVID19_world_map extends Component {

    constructor(){
        super()
        this.state = {
            word : '12312312'
        }
        this.onEachCountry = this.onEachCountry.bind(this)
        this.onCountryMouseover = this.onCountryMouseover.bind(this)
    }

    onEachCountry(country, layer){
        const countryName = country.properties.ADMIN
        layer.bindPopup(countryName)
        layer.on({
            click: this.onCountryMouseover, 
            mouseover:this.onCountryMouseover
        })

    }

    onCountryMouseover(event) {
        event.target.setStyle({
            color:'green',
            fillColor: 'yellow'
        })
    }

    render() {
        var position = [100, 100]
        return ( 
            <div> 
                <PageNavbar active='covid19_world_map'/>            
                {/* <MapContainer style={{height:"80vh"}} zoom={3} center={[20, 100]} scrollWheelZoom={false}>
                    <GeoJSON 
                        data={GeojsonData.features}
                        style={() => ({
                        color: '#4a83ec',
                        weight: 0.5,
                        fillColor: "#1a1d62",
                        fillOpacity: 1,
                        })}
                        onEachFeature = {this.onEachCountry}
                    />

                </MapContainer> */}
                <MapContainer center={position} zoom={13} scrollWheelZoom={false}>
    <TileLayer
      attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    />
    <Marker position={position}>
      <Popup>
        A pretty CSS3 popup. <br /> Easily customizable.
      </Popup>
    </Marker>
  </MapContainer>,
            </div>
        )
    }
}
