import React, { Component } from "react";
import "../style/Posters.css";
export default class PosterEachMovie extends Component {
  constructor(props) {
    super();
    this.state = {
      movie: [],
    };
  }

  componentDidMount() {
    let moviePic =
      "http://www.omdbapi.com/?i=" + this.props.imdb_id + "&apikey=e2b14d16";
    fetch(moviePic, { method: "GET" })
      .then((res) => res.json())
      .then((movieInfo) => this.setState({ movie: movieInfo }));
  }

