    // }

    onEachCountry(country, layer){
        const countryName = country.properties.ADMIN
        layer.bindPopup(countryName)
        layer.on({

        })
        var index = 0;
        console.log(this.state.hashMap.has(countryName) === true)
        layer.options.fillColor = this.state.color[this.state.hashMap.has(countryName) === true ? this.state.hashMap.get(countryName)[3] : 3]
        // if (this.state.hashMap.has(countryName)) {
        //     let countryInfo = this.state.hashMap.get(countryName)
        //     // let colorPeriod = this.state.colorPeriod
