        //     // }
        //     console.log(countryInfo[3])
        //     layer.options.fillColor = this.state.color[10]
        // }
        // // console.log(this.state.color[index], new Date().toLocaleString(),)
        // // layer.options.fillColor = this.state.color[index]

    }

    // onCountryMouseover(event) {
    //     event.target.setStyle({
    //         color:'green',
    //         fillColor: 'yellow'
    //     })
    // }

    handleCountryNameChange(event){
        this.setState({
            [event.target.name] : event.target.value
        })

    }

    handleSearch(){
        this.setState({
            center : []
        })
    }

    render() {
        return ( 
            <div> 
                <PageNavbar active='covid19_world_map'/>
                <MapContainer style={{height:"100vh"}} zoom={3} center={this.state.center} scrollWheelZoom={false}>
                <div className='searchBar'>
                    <input type='text' name="countryName" placeholder="Country name" onChange={this.handleCountryNameChange}></input>
                    <input type='text' name="date" placeholder="Date, e.g. 9/30/20" onChange={this.handleCountryNameChange}></input>
                    <button onClick={this.handleSearch}>Search</button>
                </div>  
                    <GeoJSON 
                        data={GeojsonData.features}
                        style={() => ({
                        color: '#4a83ec',
                        weight: 0.5,
                        fillColor: "white",
                        fillOpacity: 1,
                        })}
                        onEachFeature = {this.onEachCountry}
                    />

                </MapContainer>
            </div>
        )
    }
}
