import React from "react";
import PageNavbar from "./PageNavbar";
import BestGenreRow from "./BestGenreRow";
import "../style/BestGenres.css";
import "bootstrap/dist/css/bootstrap.min.css";

export default class BestGenre extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedDecade: "",
      decades: [],
      genres: [],
    };

    this.submitDecade = this.submitDecade.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  /* ---- Q3a (Best Genres) ---- */
  componentDidMount() {
    fetch("http://localhost:8081/decades", { match: "GET" })
      .then((res) => res.json())
      .then((decadeList) => {
        if (!decadeList) {
          return;
        }
        let decadeTmpList = decadeList.map((item, i) => item.decade);
        this.setState({
          decades: decadeTmpList,
        });
        console.log(this.state.decades);
      });
  }

  handleChange(e) {
    this.setState({
      selectedDecade: e.target.value,
    });
  }

  /* ---- Q3b (Best Genres) ---- */
  submitDecade() {}

  render() {
    // const tmplsit = this.state.decades.map(item => <option value={item}> {item} <option/>)
    return (
      <div className="BestGenres">
        <PageNavbar active="bestgenres" />

        <div className="container bestgenres-container">
          <div className="jumbotron">
            <div className="h5">Best Genres</div>

            <div className="years-container">
              <div className="dropdown-container">
                <select
                  value={this.state.selectedDecade}
                  onChange={this.handleChange}
                  className="dropdown"
                  id="decadesDropdown"
                >
                  <option select value>
                    {" "}
                    -- select an option --
                  </option>
                  {this.state.decades.map((item, i) => (
                    <option value={item}>{item} </option>
                  ))}
                  {/* {options.map(({ value, label }, index) => <option value={value} >{label}</option>)} */}
                </select>
                <button
                  className="submit-btn"
                  id="decadesSubmitBtn"
                  onClick={this.submitDecade}
                >
                  Submit
                </button>
              </div>
            </div>
          </div>
          <div className="jumbotron">
            <div className="movies-container">
              <div className="movie">
                <div className="header">
                  <strong>Genre</strong>
                </div>
                <div className="header">
                  <strong>Average Rating</strong>
                </div>
              </div>
              <div className="movies-container" id="results">
                {this.state.genres}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
