import React, { Component } from "react";
import PageNavbar from "./PageNavbar";
import PosterEachMovie from "./PosterEachMovie";
export default class Posters extends Component {
  constructor() {
    super();
    this.state = {
      Movies: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:8081/posters", { method: "GET" })
      .then((res) => res.json())
      .then((moviesPostersList) => {
        if (!moviesPostersList) return;
        else {
          let generateMoviesPosters = moviesPostersList.map((posterObj, i) => (
            <PosterEachMovie
              key={posterObj.id}
              id={posterObj.id}
              title={posterObj.title}
              rating={posterObj.rating}
              imdb_id={posterObj.imdb_id}
              release_year={posterObj.release_year}
            />
          ));
          this.setState({
            Movies: generateMoviesPosters,
          });
        }

        console.log(moviesPostersList);
      });
  }

  render() {
    return (
      <div>
        <PageNavbar />
        <div>{this.state.Movies}</div>
      </div>
    );
  }
}
