import React from "react";
import PageNavbar from "./PageNavbar";
import BestGenreRow from "./BestGenreRow";
import "../style/BestGenres.css";
import "bootstrap/dist/css/bootstrap.min.css";

export default class BestGenre extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedDecade: "",
      decades: [],
      genres: [],
    };

    this.submitDecade = this.submitDecade.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  /* ---- Q3a (Best Genres) ---- */
  componentDidMount() {
    fetch("http://localhost:8081/decades", { match: "GET" })
      .then((res) => res.json())
      .then((decadeList) => {
        if (!decadeList) {
          return;
        }
        let decadeTmpList = decadeList.map((item, i) => item.decade);
        this.setState({
          decades: decadeTmpList,
        });
      });
  }

  handleChange(e) {
    this.setState({
      selectedDecade: e.target.value,
    });
  }

  /* ---- Q3b (Best Genres) ---- */
  submitDecade() {
    fetch("http://localhost:8081/decades/" + this.state.selectedDecade, {
      match: "GET",
    })
      .then((res) => res.json())
      .then((bestgenreList) => {
        console.log(bestgenreList);
        bestgenreList.map((item, i) => (
          <BestGenreRow
            genre={item.genre}
            avg_rating={item.avg_rating}
            key={i}
          />
        ));
      });
  }

  render() {
    return (
      <div className="BestGenres">
        <PageNavbar active="bestgenres" />

        <div className="container bestgenres-container">
          <div className="jumbotron">
            <div className="h5">Best Genres</div>

            <div className="years-container">
              <div className="dropdown-container">
                <select
                  value={this.state.selectedDecade}
                  onChange={this.handleChange}
                  className="dropdown"
                  id="decadesDropdown"
                >
                  <option select value>
                    {" "}
                    -- select an option --
                  </option>
                  {this.state.decades.map((item, i) => (
                    <option key={i} value={item}>
                      {item}{" "}
                    </option>
                  ))}
                </select>
                <button
                  className="submit-btn"
                  id="decadesSubmitBtn"
                  onClick={this.submitDecade}
                >
                  Submit
                </button>
              </div>
            </div>
          </div>
          <div className="jumbotron">
            <div className="movies-container">
              <div className="movie">
                <div className="header">
                  <strong>Genre</strong>
                </div>
                <div className="header">
                  <strong>Average Rating</strong>
                </div>
              </div>
              <div className="movies-container" id="results">
                {this.state.genres}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
