import React, { Component } from "react";

export default class PosterEachMovie extends Component {
  constructor(props) {
    super();
  }
  render() {
    let moviePic =
      "http://img.omdbapi.com/?i=" + this.props.imdb_id + "&apikey=e2b14d16";
    return (
      <div>
        <img src={moviePic} />
        <h1>{this.props.title}</h1>
      </div>
    );
  }
}
